con:config.script:
import jxl.*
def Name, Meta_Title, Description
def filepath = "C:/Users/DineshMohanasundaram/Documents/ProductCategoryDetails.xls"
File Excel = new File(filepath)
Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet1")

def rowsize = sh.getRows().toInteger();
def columnsize = sh.getColumns().toInteger();

log.info "$rowsize and $columnsize"

//loop over the required row and column size to get the test data(Product Category details) from excel
for(def i=1; i<rowsize; i++)
{
	for(def j=0; j<columnsize; j++)
{
				if(j==0)
				{
					Name=sh.getCell(j,i).getContents()
					log.info "$Name"
				}
				else if (j==1)
				{
					Meta_Title=sh.getCell(j,i).getContents()
					log.info "$Meta_Title"
				}
				else if (j==2)
				{
					Description=sh.getCell(j,i).getContents()
					log.info "$Description"
				}

}


// set category details to customer properties at Testcase level
context.testCase.setPropertyValue("Name", Name)
context.testCase.setPropertyValue("Meta_Title", Meta_Title)
context.testCase.setPropertyValue("Description", Description)

}

