con:config.script:
import jxl.*
import com.eviware.soapui.model.testsuite.TestRunner.Status
def FirstName, LastName, Email, Password, Confirm, Telephone
def filepath = "C:/Users/DineshMohanasundaram/Documents/CustomerDetails.xls"
File Excel = new File(filepath)
Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet1")

def rowsize = sh.getRows().toInteger();
def columnsize = sh.getColumns().toInteger();

log.info "$rowsize and $columnsize"

//loop over the required row and column size to get the customer details(FirstName, LastName, Email, Password, Confirm, Telephone) from excel
for(def i=1; i<rowsize; i++)
{
	for(def j=0; j<columnsize; j++)
{
				if(j==0)
				{
					FirstName=sh.getCell(j,i).getContents()
					log.info "$FirstName"
				}
				else if (j==1)
				{
					LastName=sh.getCell(j,i).getContents()
					log.info "$LastName"
				}
				else if (j==2)
				{
					Email=sh.getCell(j,i).getContents()
					log.info "$Email"
				}
				else if (j==3)
				{
					Password=sh.getCell(j,i).getContents()
					log.info "$Password"
				}
				else if (j==4)
				{
					Confirm=sh.getCell(j,i).getContents()
					log.info "$Confirm"
				}
				else if (j==5)
				{
					Telephone=sh.getCell(j,i).getContents()
					log.info "$Telephone"
				}


}

// get TestCase
def tc = testRunner.testCase.testSuite.project.testSuites["Ecommerce End-End Functional Testing"].testCases["Add Delete Customer By Id"]

// set customer details to customer properties
context.testCase.setPropertyValue("FirstName", FirstName)
context.testCase.setPropertyValue("LastName", LastName)
context.testCase.setPropertyValue("Email", Email)
context.testCase.setPropertyValue("Password", Password)
context.testCase.setPropertyValue("Confirm", Confirm)
context.testCase.setPropertyValue("Telephone", Telephone)

// run test synchrouously
def runner = tc.run( null, false )

// show the test case result status
log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
log.info "Number of test steps which ran: " + runner.results.size()

// assert that it didn't fail
assert runner.status != Status.FAILED : runner.reason

}

